        // Card quản lý khách hàng
        view.findViewById(R.id.manage_customers_card).setOnClickListener(v ->
                Navigation.findNavController(v).navigate(R.id.action_adminDashboardFragment_to_adminCustomersFragment)
        );

        // Nút logout admin
        view.findViewById(R.id.admin_logout_btn).setOnClickListener(v -> {
            sharedPreferences.edit()
                    .putBoolean("is_logged_in", false)
                    .putBoolean("is_admin", false)
                    .apply();

            Intent intent = new Intent(requireActivity(), MainActivity.class);
            startActivity(intent);
            requireActivity().finish();
        });

        return view;
    }
}

