package com.example.bookstore.ui.fragments;

import android.app.AlertDialog;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ArrayAdapter;
import android.widget.Button;
import android.widget.LinearLayout;
import android.widget.Spinner;
import android.widget.TextView;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.example.bookstore.R;
import com.example.bookstore.ui.adapters.AdminOrderAdapter;
import com.example.bookstore.models.Order;
import com.google.android.material.tabs.TabLayout;
import java.util.ArrayList;
import java.util.List;

public class AdminOrdersFragment extends Fragment {

    private RecyclerView ordersRecyclerView;
    private AdminOrderAdapter adapter;
    private List<Order> orderList;
    private List<Order> filteredOrderList;
    private TabLayout statusTabLayout;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_admin_orders, container, false);

        ordersRecyclerView = view.findViewById(R.id.admin_orders_recycler);
        statusTabLayout = view.findViewById(R.id.order_status_tabs);

        setupTabs();
        setupRecyclerView();
        loadOrders();

        return view;
    }

    private void setupTabs() {
        statusTabLayout.addTab(statusTabLayout.newTab().setText("Tất cả"));
        statusTabLayout.addTab(statusTabLayout.newTab().setText("Chờ xử lý"));
        statusTabLayout.addTab(statusTabLayout.newTab().setText("Đang giao"));
        statusTabLayout.addTab(statusTabLayout.newTab().setText("Hoàn thành"));
        statusTabLayout.addTab(statusTabLayout.newTab().setText("Đã hủy"));

        statusTabLayout.addOnTabSelectedListener(new TabLayout.OnTabSelectedListener() {
            @Override
            public void onTabSelected(TabLayout.Tab tab) {
                filterOrdersByStatus(tab.getPosition());
            }

            @Override
            public void onTabUnselected(TabLayout.Tab tab) {}

            @Override
            public void onTabReselected(TabLayout.Tab tab) {}
        });
    }

    private void setupRecyclerView() {
        orderList = new ArrayList<>();
        filteredOrderList = new ArrayList<>();
        adapter = new AdminOrderAdapter(filteredOrderList, new AdminOrderAdapter.OnOrderActionListener() {
            @Override
            public void onChangeStatus(Order order) {
                showChangeStatusDialog(order);
            }

            @Override
            public void onCancelOrder(Order order) {
                showCancelOrderDialog(order);
            }

            @Override
            public void onViewDetails(Order order) {
                // Navigate to order details if needed
                Toast.makeText(getContext(), "Xem chi tiết đơn #" + order.getId(), Toast.LENGTH_SHORT).show();
            }
        });

        ordersRecyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        ordersRecyclerView.setAdapter(adapter);
    }

    private void loadOrders() {
        // Mock data - thay thế bằng API call thực tế
        orderList.clear();
        orderList.add(new Order("ORD001", "customer1", "Nguyễn Văn A", "123 Đường ABC", "Chờ xử lý", 450000, "2024-01-15"));
        orderList.add(new Order("ORD002", "customer2", "Trần Thị B", "456 Đường XYZ", "Đang giao", 280000, "2024-01-14"));
        orderList.add(new Order("ORD003", "customer3", "Lê Văn C", "789 Đường DEF", "Hoàn thành", 520000, "2024-01-13"));
        orderList.add(new Order("ORD004", "customer4", "Phạm Thị D", "321 Đường GHI", "Đã hủy", 180000, "2024-01-12"));
        orderList.add(new Order("ORD005", "customer5", "Hoàng Văn E", "654 Đường JKL", "Chờ xử lý", 350000, "2024-01-11"));

        filterOrdersByStatus(0); // Show all by default
    }

    private void filterOrdersByStatus(int tabPosition) {
        filteredOrderList.clear();

        if (tabPosition == 0) {
            // Tất cả
            filteredOrderList.addAll(orderList);
        } else {
            String[] statuses = {"", "Chờ xử lý", "Đang giao", "Hoàn thành", "Đã hủy"};
            String selectedStatus = statuses[tabPosition];

            for (Order order : orderList) {
                if (order.getStatus().equals(selectedStatus)) {
                    filteredOrderList.add(order);
                }
            }
        }

        adapter.notifyDataSetChanged();
    }

    private void showChangeStatusDialog(Order order) {
        AlertDialog.Builder builder = new AlertDialog.Builder(getContext());
        builder.setTitle(" Thay Đổi Trạng Thái Đơn Hàng");

        String[] statuses = {"Chờ xử lý", "Đang giao", "Hoàn thành"};
        ArrayAdapter<String> adapter = new ArrayAdapter<>(getContext(), android.R.layout.simple_spinner_item, statuses);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);

        Spinner spinner = new Spinner(getContext());
        spinner.setAdapter(adapter);

        // Set current status
        for (int i = 0; i < statuses.length; i++) {
            if (statuses[i].equals(order.getStatus())) {
                spinner.setSelection(i);
                break;
            }
        }

        LinearLayout layout = new LinearLayout(getContext());
        layout.setOrientation(LinearLayout.VERTICAL);
        layout.setPadding(50, 40, 50, 10);

        TextView label = new TextView(getContext());
        label.setText("Đơn hàng #" + order.getId() + " - " + order.getCustomerName());
        label.setTextSize(16);
        layout.addView(label);

        TextView statusLabel = new TextView(getContext());
        statusLabel.setText("Trạng thái mới:");
        statusLabel.setTextSize(14);
        statusLabel.setPadding(0, 20, 0, 10);
        layout.addView(statusLabel);

        layout.addView(spinner);

        builder.setView(layout);

        builder.setPositiveButton("✅ Cập nhật", (dialog, which) -> {
            String newStatus = statuses[spinner.getSelectedItemPosition()];
            order.setStatus(newStatus);

            // Update in the original list
            for (Order o : orderList) {
                if (o.getId().equals(order.getId())) {
                    o.setStatus(newStatus);
                    break;
                }
            }

            // Refresh current tab
            filterOrdersByStatus(statusTabLayout.getSelectedTabPosition());

            Toast.makeText(getContext(), "✅ Đã cập nhật trạng thái: " + newStatus, Toast.LENGTH_SHORT).show();
        });

        builder.setNegativeButton("❌ Hủy", null);
        builder.show();
    }

    private void showCancelOrderDialog(Order order) {
        if ("Đã hủy".equals(order.getStatus()) || "Hoàn thành".equals(order.getStatus())) {
            Toast.makeText(getContext(), "Không thể hủy đơn hàng này", Toast.LENGTH_SHORT).show();
            return;
        }

        AlertDialog.Builder builder = new AlertDialog.Builder(getContext());
        builder.setTitle("⚠️ Hủy Đơn Hàng");
        builder.setMessage("Bạn có chắc muốn hủy đơn hàng #" + order.getId() + "?");

        builder.setPositiveButton("️ Hủy đơn", (dialog, which) -> {
            order.setStatus("Đã hủy");

            // Update in the original list
            for (Order o : orderList) {
                if (o.getId().equals(order.getId())) {
                    o.setStatus("Đã hủy");
                    break;
                }
            }

            // Refresh current tab
            filterOrdersByStatus(statusTabLayout.getSelectedTabPosition());

            Toast.makeText(getContext(), "️ Đã hủy đơn hàng #" + order.getId(), Toast.LENGTH_SHORT).show();
        });

        builder.setNegativeButton("❌ Không", null);
        builder.show();
    }
}

