package com.example.bookstore.ui.fragments;

import android.app.AlertDialog;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.EditText;
import android.widget.LinearLayout;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.example.bookstore.R;
import com.example.bookstore.ui.adapters.AdminProductAdapter;
import com.example.bookstore.models.Book;
import com.google.android.material.floatingactionbutton.FloatingActionButton;
import java.util.ArrayList;
import java.util.List;

public class AdminProductsFragment extends Fragment {

    private RecyclerView productsRecyclerView;
    private AdminProductAdapter adapter;
    private List<Book> productList;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_admin_products, container, false);

        productsRecyclerView = view.findViewById(R.id.admin_products_recycler);
        FloatingActionButton addProductFab = view.findViewById(R.id.add_product_fab);

        setupRecyclerView();
        loadProducts();

        addProductFab.setOnClickListener(v -> showAddProductDialog());

        return view;
    }

    private void setupRecyclerView() {
        productList = new ArrayList<>();
        adapter = new AdminProductAdapter(productList, new AdminProductAdapter.OnProductActionListener() {
            @Override
            public void onEdit(Book book) {
                // Navigate to edit product fragment
                Navigation.findNavController(requireView()).navigate(R.id.action_adminProductsFragment_to_adminEditProductFragment);
            }

            @Override
            public void onDelete(Book book) {
                showDeleteConfirmDialog(book);
            }
        });

        productsRecyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        productsRecyclerView.setAdapter(adapter);
    }

    private void loadProducts() {
        // Mock data - thay thế bằng API call thực tế
        productList.clear();
        productList.add(new Book("1", "Đắc Nhân Tâm", "Dale Carnegie", 89000, "https://via.placeholder.com/300x400", "Sách self-help nổi tiếng", "Self-help", 4.5, 50));
        productList.add(new Book("2", "Sapiens", "Yuval Noah Harari", 120000, "https://via.placeholder.com/300x400", "Lược sử loài người", "History", 4.7, 30));
        productList.add(new Book("3", "Atomic Habits", "James Clear", 95000, "https://via.placeholder.com/300x400", "Thói quen nguyên tử", "Self-help", 4.6, 40));

        adapter.notifyDataSetChanged();
    }

    private void showAddProductDialog() {
        AlertDialog.Builder builder = new AlertDialog.Builder(getContext());
        builder.setTitle(" Thêm Sản Phẩm Mới");

        LinearLayout layout = new LinearLayout(getContext());
        layout.setOrientation(LinearLayout.VERTICAL);
        layout.setPadding(50, 40, 50, 10);

        EditText titleInput = new EditText(getContext());
        titleInput.setHint("Tên sách");
        layout.addView(titleInput);

        EditText authorInput = new EditText(getContext());
        authorInput.setHint("Tác giả");
        layout.addView(authorInput);

        EditText priceInput = new EditText(getContext());
        priceInput.setHint("Giá (VND)");
        priceInput.setInputType(android.text.InputType.TYPE_CLASS_NUMBER);
        layout.addView(priceInput);

        EditText categoryInput = new EditText(getContext());
        categoryInput.setHint("Thể loại");
        layout.addView(categoryInput);

        EditText descriptionInput = new EditText(getContext());
        descriptionInput.setHint("Mô tả");
        layout.addView(descriptionInput);

        builder.setView(layout);

        builder.setPositiveButton("✅ Thêm", (dialog, which) -> {
            String title = titleInput.getText().toString().trim();
            String author = authorInput.getText().toString().trim();
            String priceStr = priceInput.getText().toString().trim();
            String category = categoryInput.getText().toString().trim();
            String description = descriptionInput.getText().toString().trim();

            if (title.isEmpty() || author.isEmpty() || priceStr.isEmpty()) {
                Toast.makeText(getContext(), "Vui lòng điền đầy đủ thông tin", Toast.LENGTH_SHORT).show();
                return;
            }

            try {
                double price = Double.parseDouble(priceStr);
                Book newBook = new Book(
                    String.valueOf(System.currentTimeMillis()),
                    title,
                    author,
                    price,
                    "https://via.placeholder.com/300x400",
                    description,
                    category,
                    4.0,
                    0
                );

                productList.add(0, newBook);
                adapter.notifyItemInserted(0);
                productsRecyclerView.scrollToPosition(0);

                Toast.makeText(getContext(), "✅ Đã thêm sản phẩm: " + title, Toast.LENGTH_SHORT).show();
            } catch (NumberFormatException e) {
                Toast.makeText(getContext(), "Giá không hợp lệ", Toast.LENGTH_SHORT).show();
            }
        });

        builder.setNegativeButton("❌ Hủy", null);
        builder.show();
    }

    private void showDeleteConfirmDialog(Book book) {
        new AlertDialog.Builder(getContext())
                .setTitle("⚠️ Xác Nhận Xóa")
                .setMessage("Bạn có chắc muốn xóa sách \"" + book.getTitle() + "\"?")
                .setPositiveButton("️ Xóa", (dialog, which) -> {
                    productList.remove(book);
                    adapter.notifyDataSetChanged();
                    Toast.makeText(getContext(), "️ Đã xóa: " + book.getTitle(), Toast.LENGTH_SHORT).show();
                })
                .setNegativeButton("❌ Hủy", null)
                .show();
    }
}

