package com.example.bookstore.adapters;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import com.example.bookstore.R;
import com.example.bookstore.models.Order;
import java.util.List;

public class AdminOrderAdapter extends RecyclerView.Adapter<AdminOrderAdapter.ViewHolder> {
    private List<Order> orders;
    private OnOrderClickListener listener;

    public interface OnOrderClickListener {
        void onClick(Order order);
    }

    public AdminOrderAdapter(List<Order> orders, OnOrderClickListener listener) {
        this.orders = orders;
        this.listener = listener;
    }

    @NonNull
    @Override
    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_admin_order, parent, false);
        return new ViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
        Order order = orders.get(position);
        holder.orderId.setText("Đơn hàng #" + order.id);
        holder.orderDate.setText(order.orderDate);
        holder.totalAmount.setText(String.format("%,.0f₫", order.totalAmount));
        holder.status.setText(order.status);
        int statusColor;
        switch (order.status) {
            case "Chờ xác nhận": statusColor = holder.itemView.getContext().getColor(android.R.color.holo_orange_dark); break;
            case "Đang xử lý": statusColor = holder.itemView.getContext().getColor(android.R.color.holo_blue_dark); break;
            case "Đang giao hàng": statusColor = holder.itemView.getContext().getColor(android.R.color.holo_purple); break;
            case "Đã giao hàng": statusColor = holder.itemView.getContext().getColor(android.R.color.holo_green_dark); break;
            case "Đã hủy": statusColor = holder.itemView.getContext().getColor(android.R.color.holo_red_dark); break;
            default: statusColor = holder.itemView.getContext().getColor(android.R.color.darker_gray);
        }
        holder.status.setTextColor(statusColor);
        holder.itemView.setOnClickListener(v -> listener.onClick(order));
    }

    @Override
    public int getItemCount() { return orders.size(); }

    static class ViewHolder extends RecyclerView.ViewHolder {
        TextView orderId, orderDate, totalAmount, status;
        ViewHolder(View view) {
            super(view);
            orderId = view.findViewById(R.id.order_id);
            orderDate = view.findViewById(R.id.order_date);
            totalAmount = view.findViewById(R.id.order_total);
            status = view.findViewById(R.id.order_status);
        }
    }
}

package com.example.bookstore.adapters;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.example.bookstore.R;
import com.example.bookstore.models.Order;

import java.util.List;

public class AdminOrderAdapter extends RecyclerView.Adapter<AdminOrderAdapter.ViewHolder> {
    private final List<Order> orders;
    private final OnOrderClickListener listener;

    public interface OnOrderClickListener {
        void onClick(Order order);
    }

    public AdminOrderAdapter(List<Order> orders, OnOrderClickListener listener) {
        this.orders = orders;
        this.listener = listener;
    }

    @NonNull
    @Override
    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_admin_order, parent, false);
        return new ViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
        Order order = orders.get(position);

        holder.orderId.setText("Đơn hàng #" + order.id);
        holder.orderDate.setText(order.orderDate);
        holder.totalAmount.setText(String.format("%,.0f₫", order.total));
        holder.status.setText(order.status);

        int statusColor;
        switch (order.status) {
            case "Chờ xác nhận":
                statusColor = holder.itemView.getContext().getColor(android.R.color.holo_orange_dark);
                break;
            case "Đang xử lý":
                statusColor = holder.itemView.getContext().getColor(android.R.color.holo_blue_dark);
                break;
            case "Đang giao hàng":
                statusColor = holder.itemView.getContext().getColor(android.R.color.holo_purple);
                break;
            case "Đã giao hàng":
                statusColor = holder.itemView.getContext().getColor(android.R.color.holo_green_dark);
                break;
            case "Đã hủy":
                statusColor = holder.itemView.getContext().getColor(android.R.color.holo_red_dark);
                break;
            default:
                statusColor = holder.itemView.getContext().getColor(android.R.color.darker_gray);
        }
        holder.status.setTextColor(statusColor);

        holder.itemView.setOnClickListener(v -> listener.onClick(order));
    }

    @Override
    public int getItemCount() {
        return orders.size();
    }

    static class ViewHolder extends RecyclerView.ViewHolder {
        TextView orderId, orderDate, totalAmount, status;

        ViewHolder(@NonNull View view) {
            super(view);
            orderId = view.findViewById(R.id.order_id);
            orderDate = view.findViewById(R.id.order_date);
            totalAmount = view.findViewById(R.id.order_total);
            status = view.findViewById(R.id.order_status);
        }
    }
}

package com.example.bookstore.adapters;

import android.app.AlertDialog;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.example.bookstore.R;
import com.example.bookstore.models.Book;

import java.util.List;

public class AdminProductAdapter extends RecyclerView.Adapter<AdminProductAdapter.ViewHolder> {
    private final List<Book> books;
    private final OnProductActionListener listener;

    public interface OnProductActionListener {
        void onEdit(Book book);
        void onDelete(Book book);
    }

    public AdminProductAdapter(List<Book> books, OnProductActionListener listener) {
        this.books = books;
        this.listener = listener;
    }

    @NonNull
    @Override
    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_admin_product, parent, false);
        return new ViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
        Book book = books.get(position);

        holder.title.setText(book.title);
        holder.author.setText(book.author);
        holder.price.setText(String.format("%,.0f₫", book.price));
        holder.category.setText(book.category);
        holder.stock.setText("Kho: " + book.quantity);

        Glide.with(holder.image.getContext())
                .load(book.coverImage)
                .placeholder(R.drawable.book_placeholder)
                .error(R.drawable.book_placeholder)
                .centerCrop()
                .into(holder.image);

        holder.editBtn.setOnClickListener(v -> listener.onEdit(book));

        holder.deleteBtn.setOnClickListener(v ->
                new AlertDialog.Builder(v.getContext())
                        .setTitle("Xác nhận xóa")
                        .setMessage("Bạn có chắc muốn xóa sản phẩm này?")
                        .setPositiveButton("Xóa", (dialog, which) -> listener.onDelete(book))
                        .setNegativeButton("Hủy", null)
                        .show()
        );
    }

    @Override
    public int getItemCount() {
        return books.size();
    }

    static class ViewHolder extends RecyclerView.ViewHolder {
        ImageView image;
        TextView title, author, price, category, stock;
        Button editBtn, deleteBtn;

        ViewHolder(@NonNull View view) {
            super(view);
            image = view.findViewById(R.id.product_image);
            title = view.findViewById(R.id.product_title);
            author = view.findViewById(R.id.product_author);
            price = view.findViewById(R.id.product_price);
            category = view.findViewById(R.id.product_category);
            stock = view.findViewById(R.id.product_stock);
            editBtn = view.findViewById(R.id.edit_product_btn);
            deleteBtn = view.findViewById(R.id.delete_product_btn);
        }
    }
}

package com.example.bookstore.ui.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.example.bookstore.R;
import com.example.bookstore.adapters.AdminOrderAdapter;
import com.example.bookstore.models.Order;
import com.example.bookstore.utils.OrderManager;
import java.util.ArrayList;
import java.util.List;

public class AdminOrdersFragment extends Fragment {
    private RecyclerView recyclerView;
    private AdminOrderAdapter adapter;
    private Button backBtn, filterAll, filterPending, filterProcessing, filterShipping, filterCompleted, filterCancelled;
    private List<Order> allOrders, filteredOrders;
    private String currentFilter = "Tất cả";

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_admin_orders, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        recyclerView = view.findViewById(R.id.admin_orders_recycler);
        backBtn = view.findViewById(R.id.back_btn);
        filterAll = view.findViewById(R.id.filter_all);
        filterPending = view.findViewById(R.id.filter_pending);
        filterProcessing = view.findViewById(R.id.filter_processing);
        filterShipping = view.findViewById(R.id.filter_shipping);
        filterCompleted = view.findViewById(R.id.filter_completed);
        filterCancelled = view.findViewById(R.id.filter_cancelled);
        OrderManager orderManager = OrderManager.getInstance(requireContext());
        allOrders = orderManager.getOrders();
        filteredOrders = new ArrayList<>(allOrders);
        adapter = new AdminOrderAdapter(filteredOrders, this::onOrderClick);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        recyclerView.setAdapter(adapter);
        backBtn.setOnClickListener(v -> Navigation.findNavController(v).popBackStack());
        filterAll.setOnClickListener(v -> applyFilter("Tất cả", filterAll));
        filterPending.setOnClickListener(v -> applyFilter("Chờ xác nhận", filterPending));
        filterProcessing.setOnClickListener(v -> applyFilter("Đang xử lý", filterProcessing));
        filterShipping.setOnClickListener(v -> applyFilter("Đang giao hàng", filterShipping));
        filterCompleted.setOnClickListener(v -> applyFilter("Đã giao hàng", filterCompleted));
        filterCancelled.setOnClickListener(v -> applyFilter("Đã hủy", filterCancelled));
    }

    private void applyFilter(String status, Button selectedButton) {
        currentFilter = status;
        resetButtonStyle(filterAll); resetButtonStyle(filterPending); resetButtonStyle(filterProcessing); resetButtonStyle(filterShipping); resetButtonStyle(filterCompleted); resetButtonStyle(filterCancelled);
        selectedButton.setBackgroundColor(getResources().getColor(R.color.amber_600));
        selectedButton.setTextColor(getResources().getColor(R.color.white));
        filteredOrders.clear();
        if (status.equals("Tất cả")) {
            filteredOrders.addAll(allOrders);
        } else {
            for (Order order : allOrders) {
                if (order.status.equals(status)) filteredOrders.add(order);
            }
        }
        adapter.notifyDataSetChanged();
    }

    private void resetButtonStyle(Button button) {
        button.setBackgroundColor(getResources().getColor(android.R.color.darker_gray));
        button.setTextColor(getResources().getColor(R.color.black));
    }

    private void onOrderClick(Order order) {
        Bundle bundle = new Bundle();
        bundle.putSerializable("order", order);
        bundle.putBoolean("isAdmin", true);
        Navigation.findNavController(requireView()).navigate(R.id.orderDetailFragment, bundle);
    }
}

package com.example.bookstore.ui.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.bookstore.R;
import com.example.bookstore.adapters.AdminOrderAdapter;
import com.example.bookstore.models.Order;
import com.example.bookstore.utils.OrderManager;

import java.util.ArrayList;
import java.util.List;

public class AdminOrdersFragment extends Fragment {
    private RecyclerView recyclerView;
    private AdminOrderAdapter adapter;
    private Button backBtn, filterAll, filterPending, filterProcessing, filterShipping, filterCompleted, filterCancelled;
    private List<Order> allOrders, filteredOrders;
    private String currentFilter = "Tất cả";

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_admin_orders, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        recyclerView = view.findViewById(R.id.admin_orders_recycler);
        backBtn = view.findViewById(R.id.back_btn);
        filterAll = view.findViewById(R.id.filter_all);
        filterPending = view.findViewById(R.id.filter_pending);
        filterProcessing = view.findViewById(R.id.filter_processing);
        filterShipping = view.findViewById(R.id.filter_shipping);
        filterCompleted = view.findViewById(R.id.filter_completed);
        filterCancelled = view.findViewById(R.id.filter_cancelled);

        OrderManager orderManager = OrderManager.getInstance(requireContext());
        allOrders = orderManager.getAllOrders();
        filteredOrders = new ArrayList<>(allOrders);

        adapter = new AdminOrderAdapter(filteredOrders, this::onOrderClick);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        recyclerView.setAdapter(adapter);

        backBtn.setOnClickListener(v -> Navigation.findNavController(v).popBackStack());

        filterAll.setOnClickListener(v -> applyFilter("Tất cả", filterAll));
        filterPending.setOnClickListener(v -> applyFilter("Chờ xác nhận", filterPending));
        filterProcessing.setOnClickListener(v -> applyFilter("Đang xử lý", filterProcessing));
        filterShipping.setOnClickListener(v -> applyFilter("Đang giao hàng", filterShipping));
        filterCompleted.setOnClickListener(v -> applyFilter("Đã giao hàng", filterCompleted));
        filterCancelled.setOnClickListener(v -> applyFilter("Đã hủy", filterCancelled));
    }

    private void applyFilter(String status, Button selectedButton) {
        currentFilter = status;

        resetButtonStyle(filterAll);
        resetButtonStyle(filterPending);
        resetButtonStyle(filterProcessing);
        resetButtonStyle(filterShipping);
        resetButtonStyle(filterCompleted);
        resetButtonStyle(filterCancelled);

        selectedButton.setBackgroundColor(getResources().getColor(R.color.amber_600));
        selectedButton.setTextColor(getResources().getColor(R.color.white));

        filteredOrders.clear();
        if (status.equals("Tất cả")) {
            filteredOrders.addAll(allOrders);
        } else {
            for (Order order : allOrders) {
                if (order.status.equals(status)) {
                    filteredOrders.add(order);
                }
            }
        }
        adapter.notifyDataSetChanged();
    }

    private void resetButtonStyle(Button button) {
        button.setBackgroundColor(getResources().getColor(android.R.color.darker_gray));
        button.setTextColor(getResources().getColor(R.color.black));
    }

    private void onOrderClick(Order order) {
        Bundle bundle = new Bundle();
        bundle.putInt("orderId", order.id);
        bundle.putBoolean("isAdmin", true);
        Navigation.findNavController(requireView()).navigate(R.id.orderDetailFragment, bundle);
    }
}

package com.example.bookstore.ui.fragments;

import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.EditText;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import com.example.bookstore.R;
import com.example.bookstore.adapters.AdminProductAdapter;
import com.example.bookstore.models.Book;
import com.example.bookstore.utils.BookDataLoader;
import java.util.ArrayList;
import java.util.List;

public class AdminProductsFragment extends Fragment {
    private RecyclerView recyclerView;
    private AdminProductAdapter adapter;
    private EditText searchInput;
    private Button addProductBtn, backBtn;
    private List<Book> allProducts, filteredProducts;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_admin_products, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        recyclerView = view.findViewById(R.id.admin_products_recycler);
        searchInput = view.findViewById(R.id.search_product_input);
        addProductBtn = view.findViewById(R.id.add_product_btn);
        backBtn = view.findViewById(R.id.back_btn);
        allProducts = BookDataLoader.getAllBooks();
        filteredProducts = new ArrayList<>(allProducts);
        adapter = new AdminProductAdapter(filteredProducts, this::onEditProduct, this::onDeleteProduct);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        recyclerView.setAdapter(adapter);
        backBtn.setOnClickListener(v -> Navigation.findNavController(v).popBackStack());
        addProductBtn.setOnClickListener(v -> {
            Bundle bundle = new Bundle();
            bundle.putBoolean("isEdit", false);
            Navigation.findNavController(v).navigate(R.id.adminEditProductFragment, bundle);
        });
        searchInput.addTextChangedListener(new TextWatcher() {
            public void beforeTextChanged(CharSequence s, int start, int count, int after) {}
            public void onTextChanged(CharSequence s, int start, int before, int count) { filterProducts(s.toString()); }
            public void afterTextChanged(Editable s) {}
        });
    }

    private void filterProducts(String query) {
        filteredProducts.clear();
        if (query.isEmpty()) {
            filteredProducts.addAll(allProducts);
        } else {
            String lowerQuery = query.toLowerCase();
            for (Book book : allProducts) {
                if (book.title.toLowerCase().contains(lowerQuery) || book.author.toLowerCase().contains(lowerQuery) || book.category.toLowerCase().contains(lowerQuery)) {
                    filteredProducts.add(book);
                }
            }
        }
        adapter.notifyDataSetChanged();
    }

    private void onEditProduct(Book book) {
        Bundle bundle = new Bundle();
        bundle.putBoolean("isEdit", true);
        bundle.putSerializable("book", book);
        Navigation.findNavController(requireView()).navigate(R.id.adminEditProductFragment, bundle);
    }

    private void onDeleteProduct(Book book) {
        allProducts.remove(book);
        filteredProducts.remove(book);
        adapter.notifyDataSetChanged();
    }
}

package com.example.bookstore.ui.fragments;

import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.EditText;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.bookstore.R;
import com.example.bookstore.adapters.AdminProductAdapter;
import com.example.bookstore.models.Book;
import com.example.bookstore.utils.BookDataLoader;

import java.util.ArrayList;
import java.util.List;

public class AdminProductsFragment extends Fragment {
    private RecyclerView recyclerView;
    private AdminProductAdapter adapter;
    private EditText searchInput;
    private Button addProductBtn, backBtn;
    private List<Book> allProducts, filteredProducts;

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_admin_products, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        recyclerView = view.findViewById(R.id.admin_products_recycler);
        searchInput = view.findViewById(R.id.search_product_input);
        addProductBtn = view.findViewById(R.id.add_product_btn);
        backBtn = view.findViewById(R.id.back_btn);

        allProducts = BookDataLoader.getAllBooks();
        filteredProducts = new ArrayList<>(allProducts);

        adapter = new AdminProductAdapter(filteredProducts, new AdminProductAdapter.OnProductActionListener() {
            @Override
            public void onEdit(Book book) {
                onEditProduct(book);
            }

            @Override
            public void onDelete(Book book) {
                onDeleteProduct(book);
            }
        });

        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        recyclerView.setAdapter(adapter);

        backBtn.setOnClickListener(v -> Navigation.findNavController(v).popBackStack());

        addProductBtn.setOnClickListener(v -> {
            Bundle bundle = new Bundle();
            bundle.putBoolean("isEdit", false);
            Navigation.findNavController(v).navigate(R.id.adminEditProductFragment, bundle);
        });

        searchInput.addTextChangedListener(new TextWatcher() {
            public void beforeTextChanged(CharSequence s, int start, int count, int after) {}

            public void onTextChanged(CharSequence s, int start, int before, int count) {
                filterProducts(s.toString());
            }

            public void afterTextChanged(Editable s) {}
        });
    }

    private void filterProducts(String query) {
        filteredProducts.clear();
        if (query.isEmpty()) {
            filteredProducts.addAll(allProducts);
        } else {
            String lowerQuery = query.toLowerCase();
            for (Book book : allProducts) {
                if (book.title.toLowerCase().contains(lowerQuery)
                        || book.author.toLowerCase().contains(lowerQuery)
                        || book.category.toLowerCase().contains(lowerQuery)) {
                    filteredProducts.add(book);
                }
            }
        }
        adapter.notifyDataSetChanged();
    }

    private void onEditProduct(Book book) {
        Bundle bundle = new Bundle();
        bundle.putBoolean("isEdit", true);
        bundle.putSerializable("book", book);
        Navigation.findNavController(requireView()).navigate(R.id.adminEditProductFragment, bundle);
    }

    private void onDeleteProduct(Book book) {
        allProducts.remove(book);
        filteredProducts.remove(book);
        adapter.notifyDataSetChanged();
    }
}
